    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       String liczba1 = jTextFieldLiczba1.getText();
       int i = Integer.parseInt(liczba1);
       
       String liczba2 = jTextFieldLiczba2.getText();
       int  j = Integer.parseInt(liczba2);
      
       if(operacja.getSelectedIndex()==0){
                int wynik = i+j;
       
       String wynikString = Integer.toString(wynik);
       
       jTextFieldWynik.setText(wynikString); 
       }else if(operacja.getSelectedIndex()==1){
                int wynik = i-j;
       
       String wynikString = Integer.toString(wynik);
       
       jTextFieldWynik.setText(wynikString); 
       }else if(operacja.getSelectedIndex()==2){
                int wynik = i*j;
       
       String wynikString = Integer.toString(wynik);
       
       jTextFieldWynik.setText(wynikString); 
       }else{
        double wynik = 1.0*i/j;
       
       String wynikString = Double.toString(wynik);
       
       jTextFieldWynik.setText(wynikString); 
        }
    }//GEN-LAST:event_jButton1ActionPerformed

